public class SwitchDemo2 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		char j;
		// TODO Auto-generated method stub
		char ch= 'j' ;
				
		switch(ch)		{
		case 'a':
			System.out.println("Print case a");
			break;
			
		case 'x':
			System.out.println("Print case x");
			break;
			
		case 'h':
			System.out.println("Print case h");
			break;
			
		case 'c':
			System.out.println("Print case c");
			break;
		}
		System.out.println("All the cases are wrong");
		
		}
	
	}



